var link=document.querySelector(".footer-button"),popup=document.querySelector(".modal-feedback"),close=popup.querySelector(".modal-close"),form=popup.querySelector("form"),guestname=popup.querySelector("[name=full-name]"),guestemail=popup.querySelector("[name=e-mail]"),isStorageSupport=!0,storage="";try{storage=localStorage.getItem("login")}catch(e){isStorageSupport=!1}link.addEventListener("click",function(e){e.preventDefault(),popup.classList.add("modal-show"),guestname.focus()}),close.addEventListener("click",function(e){e.preventDefault(),popup.classList.remove("modal-show")}),form.addEventListener("submit",function(e){guestname.value&&guestemail.value?isStorageSupport&&localStorage.setItem("guestname",guestname.value):(e.preventDefault(),console.log("Нужно заполнить поля"))}),window.addEventListener("keydown",function(e){27===e.keyCode&&(e.preventDefault(),popup.classList.contains("modal-show")&&popup.classList.remove("modal-show"))});